query:
query users($where: UserWhereInput, $orderBy: UserOrderByInput, $skip: Float, $take: Float) {
  items: users(where: $where, orderBy: $orderBy, skip: $skip, take: $take) {
    createdAt
    firstName
    id
    lastName
    roles
    updatedAt
    username
    __typename
  }
  total: _usersMeta(where: $where, orderBy: $orderBy, skip: $skip, take: $take) {
    count
    __typename
  }
}
